import { useState } from "react";

function App() {
  const [topic, setTopic] = useState("");
  const [caption, setCaption] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);

  const generateCaption = async () => {
    setLoading(true);
    try {
      const res = await fetch("http://localhost:10000/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ topic }),
      });

      const data = await res.json();
      setCaption(data.caption || "No caption found.");
      setImageUrl(data.imageUrl || "");
    } catch (err) {
      console.error(err);
      setCaption("Error generating meme.");
    }
    setLoading(false);
  };

  return (
    <div style={{
      minHeight: "100vh",
      background: "#1c1c1c",
      color: "#fff",
      fontFamily: "Arial, sans-serif",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      padding: "40px"
    }}>
      <div style={{
        background: "#333",
        padding: "30px",
        borderRadius: "20px",
        boxShadow: "0 0 20px rgba(0,0,0,0.6)",
        maxWidth: "700px",
        width: "100%",
        textAlign: "center"
      }}>
        <h1 style={{ color: "#FFD700", fontSize: "2.5rem", marginBottom: "20px" }}>AI Meme Generator ðŸ˜Ž</h1>

        <input
          type="text"
          placeholder="Enter meme topic"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          style={{
            padding: "12px",
            width: "80%",
            fontSize: "16px",
            marginBottom: "20px",
            borderRadius: "10px",
            border: "none"
          }}
        />
        <br />
        <button
          onClick={generateCaption}
          style={{
            padding: "12px 24px",
            backgroundColor: "#ff007f",
            border: "none",
            borderRadius: "20px",
            fontSize: "16px",
            fontWeight: "bold",
            color: "white",
            cursor: "pointer",
            marginBottom: "30px"
          }}
        >
          {loading ? "Generating..." : "Generate Caption"}
        </button>

        {imageUrl && (
          <div>
            <img
              src={imageUrl}
              alt="Generated Meme"
              style={{
                width: "100%",
                maxWidth: "500px",
                borderRadius: "10px",
                marginBottom: "20px",
                boxShadow: "0 0 10px rgba(0,0,0,0.5)"
              }}
            />
          </div>
        )}

        {caption && (
          <p style={{
            background: "#222",
            padding: "20px",
            borderRadius: "10px",
            fontWeight: "bold",
            fontSize: "18px",
            color: "#f1f1f1",
            boxShadow: "0 0 10px rgba(0,0,0,0.3)"
          }}>{caption}</p>
        )}
      </div>
    </div>
  );
}

export default App;
