import { useState } from "react";

function App() {
  const [topic, setTopic] = useState("");
  const [caption, setCaption] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [loading, setLoading] = useState(false);

  const generateCaption = async () => {
    setLoading(true);
    try {
      const res = await fetch("https://ai-meme-generator-9r8v.onrender.com", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ topic }),
      });

      const data = await res.json();
      setCaption(data.caption || "No caption found.");
      setImageUrl(data.imageUrl || "");
    } catch (err) {
      console.error(err);
      setCaption("Error generating meme.");
    }
    setLoading(false);
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        background: "#1c1c1c",
        color: "#fff",
        fontFamily: "Arial, sans-serif",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        padding: "40px",
      }}
    >
      <div
        style={{
          background: "#333",
          padding: "30px",
          borderRadius: "20px",
          boxShadow: "0 0 20px rgba(0,0,0,0.6)",
          maxWidth: "700px",
          width: "100%",
          textAlign: "center",
          transition: "transform 0.3s ease",
        }}
      >
        <h1
          style={{
            color: "#FFD700",
            fontSize: "2.5rem",
            marginBottom: "20px",
            transition: "color 0.3s ease",
          }}
        >
          AI Meme Generator ðŸ˜Ž
        </h1>

        <input
          type="text"
          placeholder="Enter meme topic"
          value={topic}
          onChange={(e) => setTopic(e.target.value)}
          style={{
            padding: "12px",
            width: "80%",
            fontSize: "16px",
            marginBottom: "20px",
            borderRadius: "10px",
            border: "none",
            transition: "all 0.3s ease",
          }}
        />
        <br />
        <button
          onClick={generateCaption}
          style={{
            padding: "12px 24px",
            backgroundColor: "#ff007f",
            border: "none",
            borderRadius: "20px",
            fontSize: "16px",
            fontWeight: "bold",
            color: "white",
            cursor: "pointer",
            marginBottom: "30px",
            transition: "transform 0.3s ease",
          }}
          onMouseEnter={(e) => (e.target.style.transform = "scale(1.05)")}
          onMouseLeave={(e) => (e.target.style.transform = "scale(1)")}
        >
          {loading ? "Generating..." : "Generate Caption"}
        </button>

        {imageUrl && (
          <img
            src={imageUrl}
            alt="Generated Meme"
            style={{
              width: "100%",
              maxWidth: "500px",
              borderRadius: "10px",
              marginBottom: "20px",
              boxShadow: "0 0 10px rgba(0,0,0,0.5)",
              opacity: 1,
              animation: "fadeIn 0.8s ease-in",
            }}
          />
        )}

        {caption && (
          <p
            style={{
              background: "#222",
              padding: "20px",
              borderRadius: "10px",
              fontWeight: "bold",
              fontSize: "18px",
              color: "#f1f1f1",
              boxShadow: "0 0 10px rgba(0,0,0,0.3)",
              opacity: 1,
              animation: "fadeIn 0.8s ease-in",
            }}
          >
            {caption}
          </p>
        )}
      </div>

      {/* Simple fade-in animation added as global style */}
      <style>{`
        @keyframes fadeIn {
          0% { opacity: 0; transform: scale(0.95); }
          100% { opacity: 1; transform: scale(1); }
        }
      `}</style>
    </div>
  );
}

export default App;
